n=1.49888;data = 1-([2.88, 1.46, .493, .147, .387, 1.18, 2.41, 4.02, 5.67] / 5.67);g1 = [];g2 = [];for ti = ([10:10:90] * pi) / 180  %g1 = [g1, abs((cos(ti) - sqrt(n^2-(sin(ti))^2))/(cos(ti) + sqrt(n^2-(sin(ti))^2)))];  %g2 = [g2, abs((-n^2 * cos(ti) + sqrt(n^2-(sin(ti))^2))/(n^2 * cos(ti) + sqrt(n^2-(sin(ti))^2)))];  g1 = [g1, ((cos(ti) - sqrt(n^2-(sin(ti))^2))/(cos(ti) + sqrt(n^2-(sin(ti))^2)))];  g2 = [g2, ((-n^2 * cos(ti) + sqrt(n^2-(sin(ti))^2))/(n^2 * cos(ti) + sqrt(n^2-(sin(ti))^2)))];endforplot([10:10:90], (1-(g1.^2+g2.^2)/2), 'b-o');title('Reflected Beam Intensity vs. Incident Angle');ylabel('Reflected Beam Intensity (Normalized)');xlabel('Incident Angle');hold on;